const selectStock=$("#stockSelect"),getStock=()=>{const n=selectStock.find("option:selected").text();fetch("/home/getStock",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({stock:n})}).then(n=>{if(!n.ok)throw new Error("Network response was not ok");return n.json()}).then(()=>{}).catch(n=>{console.error("Fetch Error:",n)})};selectStock.on("change",getStock);const connection=(new signalR.HubConnectionBuilder).withUrl("/stockHub").build();connection.on("ReceiveMessage",function(n){$("#showPrice").text(n)});connection.start().catch(function(n){return console.error(n.toString())});