const selectStock=$("#stockSelect"),getStock=()=>{const n=selectStock.find("option:selected").text();fetch("/home/getStock",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({stock:n})}).then(n=>{if(!n.ok)throw new Error("Network response was not ok");return n.json()}).then(n=>{console.log(n)}).catch(n=>{console.error("Fetch Error:",n)})};selectStock.change(getStock);